$("#form-contact").on("submit",function(e){e.preventDefault();var a=$("input[name=name]").val(),t=$("input[name=email").val(),n=$("input[name=_subject").val(),o=$("textarea[name=message]").val();$.ajax({url:"https://formspree.io/{{ .Site.Params.email }}",method:"POST",data:{name:a,_replyto:t,email:t,_subject:n,message:o},dataType:"json",success:function(){console.log("Form successfully sent"),$("#form-contact").get(0).reset(),$("#form-submit").fadeOut(function(){$("#form-thankyou").fadeIn(function(){$("#form-thankyou").delay(4e3).fadeOut(function(){$("#form-submit").fadeIn()})})})}})});